class Carousel {
  final String title;
  final List<CardDetails> cardDetails;

  const Carousel
  (
    {
      required this.title,
      required this.cardDetails,
    }
  );
}

class CardDetails {
  final String image;
  final String name;
  final String desc;
  final double price;
  bool isFavorite;
  
  CardDetails
  (
    {
      required this.image,
      required this.name,
      required this.desc,
      required this.price,
      required this.isFavorite,
    }
  );
}